package ch01;

public class ArthmeticOPTest3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		System.out.println("(++a + ++a)의 결과는:" +(++a + ++a) );
		a = 10;
		System.out.println("(++a - ++a)의 결과는:" +(++a - ++a) );
		a = 10;
		System.out.println("(a++ + a++)의 결과는:" +(a++ + a++));

	}

}
